	subroutine SquaredMEHel(HelInd(vmax,res), flags)
	implicit none
	SIMD_ONLY(integer vmax)
	ResType res(HelDim(*))
	integer flags

#include "vars.h"

	HelType amp
	ResType ampsq
#if DEBUG >= 3
	SIMD_ONLY(integer v)
#endif

* BEGIN ABBR_HEL
	TEST(flags, BIT_LOOP)
	call abbr1h1
	call abbr1h2
	call abbr1h3
	call abbr1h4
	call abbr1h5
	ENDTEST(flags, BIT_LOOP)
* END ABBR_HEL

	ampsq = 0
	res(HelAll(1)) = ampsq

	ampsq = 0
	TEST(flags, BIT_LOOP)
* BEGIN FF_LOOP
	Cloop(HelAll(1)) = 0

	call born
	call box
	call penta
* END FF_LOOP

* BEGIN M2_LOOP
	amp = 0
	amp = amp + Cloop(HelAll(1))*MatSUN(1,1)
	ampsq = ampsq + Re(amp*Conjugate(Cloop(HelAll(1))))
* END M2_LOOP
	ENDTEST(flags, BIT_LOOP)
	res(HelAll(2)) = ampsq

#if DEBUG >= 3
	print 1, HelLoop(Hel(HelInd(v,1:LEGS)), res(HelInd(v,1:2)), v,vmax)
1	format(' sqme(', LEGS I3, ') =', 2F25.12)
#endif
	end

