* BEGIN HSEL_IF
	  if( hseltest(i) .lt. hselmin ) cycle
* END HSEL_IF

#define hval(s,m,a,b) a*mod(i/s,m)-b

	  Hel0(1) = hval(4,2,2,1)
	  Hel0(2) = hval(2,2,2,1)
	  Hel0(3) = 0
	  Hel0(4) = 0
	  Hel0(5) = hval(1,2,2,1)
